// Parse Telegram message and determine action
const msg = $input.first().json.message;
const text = (msg.text || '').trim();
const chatId = msg.chat.id;
const username = msg.from.first_name || 'User';

let action = 'add_task';
let taskId = null;
let taskTitle = text;

// Check for DONE command: "DONE 3" or "done 5"
const doneMatch = text.match(/^done\s+(\d+)/i);
if (doneMatch) {
  action = 'complete';
  taskId = parseInt(doneMatch[1]);
  taskTitle = '';
}
// Check for LIST command
else if (text.toLowerCase() === '/list' || text.toLowerCase() === 'list') {
  action = 'list';
  taskTitle = '';
}
// Check for HELP command
else if (text.toLowerCase() === '/help' || text.toLowerCase() === 'help') {
  action = 'help';
  taskTitle = '';
}
// Check for /start
else if (text.toLowerCase() === '/start') {
  action = 'help';
  taskTitle = '';
}

return {
  json: {
    action: action,
    taskId: taskId,
    taskTitle: taskTitle,
    chatId: chatId,
    username: username
  }
};

// Calculate next row number and format task data
const allRows = $input.all();
const commandData = $('Parse Command').first().json;

// Next row number = total rows + 1 (since row 1 is header)
const nextRowNum = allRows.length + 1;

// Parse priority from task title if mentioned
let priority = 'Medium';
let title = commandData.taskTitle;

if (title.toLowerCase().includes('urgent') || title.toLowerCase().includes('high')) {
  priority = 'High';
  title = title.replace(/\b(urgent|high priority|high)\b/gi, '').trim();
} else if (title.toLowerCase().includes('low')) {
  priority = 'Low';
  title = title.replace(/\b(low priority|low)\b/gi, '').trim();
}

// Set due date (default: 7 days from now)
const dueDate = new Date();
dueDate.setDate(dueDate.getDate() + 7);

return {
  json: {
    row_number: nextRowNum,
    taskTitle: title || 'Untitled Task',
    description: '',
    priority: priority,
    dueDate: dueDate.toISOString().split('T')[0],
    status: 'Pending',
    owner: 'Dhruv',
    createdAt: new Date().toISOString(),
    lastReminderSent: '',
    reminderCount: 0,
    chatId: commandData.chatId
  }
};

