// Format task list
const allTasks = $input.all();
const chatId = $('Parse Command').first().json.chatId;

const priorityEmoji = { 'High': 'üî¥', 'Medium': 'üü†', 'Low': 'üü¢' };

// Filter pending tasks
const pending = allTasks.filter(t => t.json.status !== 'Completed');

if (pending.length === 0) {
  return {
    json: {
      message: 'üìã *Your Tasks*\n\nüéâ No pending tasks! You\'re all caught up.',
      chatId: chatId
    }
  };
}

let taskList = pending.slice(0, 10).map(t => {
  const task = t.json;
  const emoji = priorityEmoji[task.priority] || 'üìã';
  return `${emoji} *#${task.row_number}:* ${task.taskTitle}\n   _Due: ${task.dueDate} | ${task.status}_`;
}).join('\n\n');

const message = `üìã *Your Pending Tasks* (${pending.length})\n\n${taskList}\n\n_Reply \`DONE <number>\` to complete_`;

return {
  json: {
    message: message,
    chatId: chatId
  }
};

// Filter tasks that need reminders
const allTasks = $input.all();
const now = new Date();
const tasksToRemind = [];

for (const item of allTasks) {
  const task = item.json;

  // Skip completed tasks
  if (task.status === 'Completed') continue;

  // Skip tasks without chatId
  if (!task.chatId) continue;

  // Check if we should send reminder
  let shouldRemind = false;
  const lastReminder = task.lastReminderSent ? new Date(task.lastReminderSent) : null;
  const reminderCount = parseInt(task.reminderCount) || 0;

  // Check if 2+ hours since last reminder (avoid spam)
  if (!lastReminder) {
    shouldRemind = true; // First reminder
  } else {
    const hoursSinceLast = (now - lastReminder) / (1000 * 60 * 60);
    if (hoursSinceLast >= 2) {
      shouldRemind = true;
    }
  }

  if (shouldRemind) {
    // Calculate urgency
    let urgency = 'normal';
    let emoji = 'üìã';

    // Check if overdue
    const dueDate = task.dueDate ? new Date(task.dueDate) : null;
    const isOverdue = dueDate && dueDate < now;

    if (isOverdue) {
      urgency = 'critical';
      emoji = 'üî•';
    } else if (reminderCount >= 5) {
      urgency = 'critical';
      emoji = 'üö®';
    } else if (reminderCount >= 3) {
      urgency = 'warning';
      emoji = '‚ö†Ô∏è';
    }

    // Priority emoji
    const priorityEmoji = { 'High': 'üî¥', 'Medium': 'üü†', 'Low': 'üü¢' };
    const pEmoji = priorityEmoji[task.priority] || 'üü†';

    tasksToRemind.push({
      row_number: task.row_number,
      taskTitle: task.taskTitle,
      priority: task.priority,
      priorityEmoji: pEmoji,
      dueDate: task.dueDate,
      status: task.status,
      chatId: task.chatId,
      reminderCount: reminderCount + 1,
      urgency: urgency,
      urgencyEmoji: emoji,
      isOverdue: isOverdue
    });
  }
}

// Return tasks to remind (or empty array)
if (tasksToRemind.length === 0) {
  return [{ json: { skip: true } }];
}

return tasksToRemind.map(t => ({ json: t }));

