// Generate daily summary
const allTasks = $input.all();
const now = new Date();

let total = allTasks.length;
let pending = 0;
let completed = 0;
let overdue = 0;
let highPriority = 0;
let chatId = '';

for (const item of allTasks) {
  const task = item.json;

  // Get chatId from first task
  if (!chatId && task.chatId) {
    chatId = task.chatId;
  }

  if (task.status === 'Completed') {
    completed++;
  } else {
    pending++;

    if (task.priority === 'High') {
      highPriority++;
    }

    // Check overdue
    if (task.dueDate) {
      const dueDate = new Date(task.dueDate);
      if (dueDate < now) {
        overdue++;
      }
    }
  }
}

const completionRate = total > 0 ? Math.round((completed / total) * 100) : 0;

const message = `‚òÄÔ∏è *Good Morning, Dhruv!*\n\n` +
  `üìä *DAILY TASK SUMMARY*\n` +
  `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n\n` +
  `üìã *Total Tasks:* ${total}\n` +
  `‚è≥ *Pending:* ${pending}\n` +
  `‚úÖ *Completed:* ${completed}\n\n` +
  `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n\n` +
  `üî• *Needs Attention:*\n` +
  `‚Ä¢ üî¥ High Priority: ${highPriority}\n` +
  `‚Ä¢ ‚ö†Ô∏è Overdue: ${overdue}\n\n` +
  `üìà *Completion Rate:* ${completionRate}%\n\n` +
  (overdue > 0 ? `_‚ö° You have ${overdue} overdue task${overdue > 1 ? 's' : ''}!_\n\n` : '') +
  `_Reply /list to see all tasks_`;

return {
  json: {
    message: message,
    chatId: chatId || 'YOUR_CHAT_ID'
  }
};
