parameters.inputSchema:
{
  "type": "object",
  "required": ["overall_score", "classification", "strengths", "gaps", "skills_present", "skills_missing", "experience_years", "recommendation_summary"],
  "properties": {
    "overall_score": {
      "type": "integer",
      "minimum": 0,
      "maximum": 100,
      "description": "Overall candidate score out of 100"
    },
    "classification": {
      "type": "string",
      "enum": ["Strong Fit", "Average", "Reject"],
      "description": "Candidate classification"
    },
    "strengths": {
      "type": "array",
      "items": {"type": "string"},
      "description": "Key strengths of the candidate"
    },
    "gaps": {
      "type": "array",
      "items": {"type": "string"},
      "description": "Key gaps or concerns"
    },
    "skills_present": {
      "type": "array",
      "items": {"type": "string"},
      "description": "Skills that match job requirements"
    },
    "skills_missing": {
      "type": "array",
      "items": {"type": "string"},
      "description": "Required skills that are missing"
    },
    "experience_years": {
      "type": "string",
      "description": "Years of relevant experience"
    },
    "recommendation_summary": {
      "type": "string",
      "description": "Brief recommendation summary"
    }
  }
}

