// Get stored task data from workflow static data
const staticData = $getWorkflowStaticData('global');

const today = new Date().toISOString().split('T')[0];

// Check if we have a task for today
if (staticData.taskDate !== today || !staticData.dailyTask) {
  return [{ json: { hasTask: false, message: 'No task set for today' } }];
}

return [{
  json: {
    hasTask: true,
    dailyTask: staticData.dailyTask,
    taskStatus: staticData.taskStatus || 'pending',
    taskDate: staticData.taskDate
  }
}];

const stoicQuote = $('Generate Stoic Quote').first().json.text;
const dailyTask = $('Check Task Status').first().json.dailyTask;

return [{
  json: {
    dailyTask: dailyTask,
    stoicQuote: stoicQuote
  }
}];

const message = $json.message.text.toLowerCase().trim();
const staticData = $getWorkflowStaticData('global');
const today = new Date().toISOString().split('T')[0];

let responseType = 'unknown';
let responseMessage = '';

// Check for completion responses
if (message.includes('‚úÖ') || message.includes('completed') || message.includes('done') || message.includes('finished')) {
  responseType = 'completed';
  staticData.taskStatus = 'completed';
  responseMessage = 'Task marked as completed!';
}
// Check for still working response
else if (message.includes('‚è≥') || message.includes('still working') || message.includes('working') || message.includes('not yet')) {
  responseType = 'working';
  responseMessage = 'Keep going! You\'ve got this.';
}
// New task input (if it's a new day or no task exists)
else if (!staticData.taskDate || staticData.taskDate !== today || staticData.taskStatus === 'completed') {
  responseType = 'new_task';
  staticData.dailyTask = $json.message.text;
  staticData.taskStatus = 'pending';
  staticData.taskDate = today;
  responseMessage = `Task set for today: "${$json.message.text}"`;
}
// Update existing task
else {
  responseType = 'update_task';
  staticData.dailyTask = $json.message.text;
  staticData.taskStatus = 'pending';
  responseMessage = `Task updated: "${$json.message.text}"`;
}

return [{
  json: {
    responseType: responseType,
    responseMessage: responseMessage,
    dailyTask: staticData.dailyTask,
    taskStatus: staticData.taskStatus,
    originalMessage: $json.message.text
  }
}];

const staticData = $getWorkflowStaticData('global');
const today = new Date().toISOString().split('T')[0];

let result = {
  hasTask: false,
  taskCompleted: false,
  dailyTask: '',
  taskStatus: ''
};

if (staticData.taskDate === today && staticData.dailyTask) {
  result.hasTask = true;
  result.dailyTask = staticData.dailyTask;
  result.taskStatus = staticData.taskStatus;
  result.taskCompleted = staticData.taskStatus === 'completed';
}

// Reset for next day
staticData.taskDate = null;
staticData.dailyTask = null;
staticData.taskStatus = null;

return [{ json: result }];

