// Set defaults for missing optional fields
const input = $input.first().json;

const topic = input.topic || input.body?.topic || 'AI Automation';
const audience = input.audience || input.body?.audience || 'Professional';
const slideCount = input.preferred_slide_count || input.body?.preferred_slide_count || 'auto';
const notes = input.additional_notes || input.body?.additional_notes || '';

// Generate folder-safe topic name
const safeTopic = topic.replace(/[^a-zA-Z0-9]/g, '_').substring(0, 50);
const date = new Date().toISOString().split('T')[0];
const fileName = `${safeTopic}_AIwithDhruv_${date}.pptx`;

return {
  json: {
    topic: topic,
    audience: audience,
    slideCount: slideCount,
    additionalNotes: notes,
    safeTopic: safeTopic,
    fileName: fileName,
    date: date,
    timestamp: new Date().toISOString()
  }
};

// Parse AI research output
const input = $input.first().json;
const defaults = $('Set Defaults').first().json;

let research = {};

try {
  // Try to extract JSON from the AI response
  const output = input.output || input.text || JSON.stringify(input);
  const jsonMatch = output.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    research = JSON.parse(jsonMatch[0]);
  } else {
    // Fallback structure if JSON not found
    research = {
      definition: output.substring(0, 500),
      keyConcepts: ['Key Concept 1', 'Key Concept 2', 'Key Concept 3'],
      useCases: [{ title: 'Use Case', description: 'Description' }],
      trends: ['Current trend'],
      benefits: ['Main benefit'],
      challenges: ['Main challenge'],
      takeaways: ['Key takeaway']
    };
  }
} catch (e) {
  research = {
    definition: 'Topic overview',
    keyConcepts: ['Concept 1', 'Concept 2', 'Concept 3'],
    useCases: [{ title: 'Use Case', description: 'Description' }],
    trends: ['Trend'],
    benefits: ['Benefit'],
    challenges: ['Challenge'],
    takeaways: ['Takeaway']
  };
}

return {
  json: {
    ...defaults,
    research: research
  }
};

// Parse slide structure and prepare for Gamma API
const input = $input.first().json;
const defaults = $('Set Defaults').first().json;
const research = $('Parse Research').first().json.research;

let slides = [];

try {
  const output = input.output || input.text || '';
  const jsonMatch = output.match(/\[[\s\S]*\]/);
  if (jsonMatch) {
    slides = JSON.parse(jsonMatch[0]);
  }
} catch (e) {
  // Fallback slides if parsing fails
  slides = [
    { slideNumber: 1, type: 'title', headline: defaults.topic, subtitle: 'A Comprehensive Overview', notes: '' },
    { slideNumber: 2, type: 'agenda', headline: 'What We\'ll Cover', content: ['Overview', 'Key Concepts', 'Use Cases', 'Takeaways'], notes: '' },
    { slideNumber: 3, type: 'content', headline: 'Understanding ' + defaults.topic, content: research.keyConcepts || ['Point 1', 'Point 2'], notes: '' },
    { slideNumber: 4, type: 'content', headline: 'Real-World Applications', content: research.useCases?.map(u => u.title) || ['Use Case 1'], notes: '' },
    { slideNumber: 5, type: 'takeaways', headline: 'Key Takeaways', content: research.takeaways || ['Takeaway 1'], notes: '' },
    { slideNumber: 6, type: 'closing', headline: 'Thank You', subtitle: 'Created with AIwithDhruv', notes: '' }
  ];
}

// Build Gamma-compatible content
const gammaContent = {
  title: defaults.topic,
  slides: slides,
  theme: {
    background: '#F8F9FA',
    primaryColor: '#6B7280',
    accentColor: '#06B6D4',
    style: 'modern-minimal'
  },
  branding: {
    footer: 'AIwithDhruv',
    logo: false
  }
};

// Build prompt for Gamma
const gammaPrompt = `Create a premium presentation about "${defaults.topic}".

Audience: ${defaults.audience}

Slide Structure:\n${slides.map(s => `Slide ${s.slideNumber}: ${s.headline}`).join('\n')}

Design: White background, metallic grey and soft neon cyan accents, premium SaaS style, executive-level quality. NO dark themes.

Branding: Include "AIwithDhruv" on closing slide.`;

return {
  json: {
    ...defaults,
    slides: slides,
    slideCount: slides.length,
    gammaContent: gammaContent,
    gammaPrompt: gammaPrompt
  }
};

// Prepare data for Google Sheets and response
const driveFile = $input.first().json;
const prepData = $('Prepare for Gamma').first().json;

const driveLink = `https://drive.google.com/file/d/${driveFile.id}/view`;

return {
  json: {
    topic: prepData.topic,
    audience: prepData.audience,
    slideCount: prepData.slideCount,
    date: prepData.date,
    driveLink: driveLink,
    driveFileId: driveFile.id,
    fileName: prepData.fileName,
    status: 'Completed',
    timestamp: new Date().toISOString()
  }
};

