parameters.jsCode:
// Get the enhanced resume content from AI output
let enhancedContent = $input.first().json.output || $input.first().json.message?.content || "";

// Clean up unwanted characters or markdown formatting
enhancedContent = enhancedContent
  .replace(/```html/gi, '')
  .replace(/```/g, '')
  .replace(/<html>/gi, '')
  .replace(/<\/html>/gi, '')
  .replace(/^[\s\S]*?<h1>/, '<h1>') // remove garbage before <h1>
  .trim();

// Define metallic-style HTML structure
const htmlTemplate = `
<!DOCTYPE html>
<html>
<head>
<meta charset="UTF-8">
<title>Enhanced Resume</title>
<style>
  body {
    font-family: 'Inter', sans-serif;
    background-color: #000000;
    color: #E5E4E2;
    margin: 40px;
    line-height: 1.6;
  }
  h1, h2, h3 {
    color: #C0C0C0;
  }
  .resume-section {
    background-color: #353E43;
    padding: 20px;
    border-radius: 12px;
    margin-bottom: 25px;
    box-shadow: 0 2px 8px rgba(192, 192, 192, 0.15);
  }
  .contact-section ul {
    list-style: none;
    padding: 0;
  }
  .contact-section li {
    margin-bottom: 6px;
  }
  .job-title {
    font-size: 18px;
    font-weight: bold;
    color: #E5E4E2;
    margin-top: 5px;
  }
  a {
    color: #C0C0C0;
    text-decoration: none;
  }
  a:hover {
    text-decoration: underline;
  }
</style>
</head>
<body>
  ${enhancedContent}
</body>
</html>
`;

// Return formatted HTML
return [{ json: { html: htmlTemplate } }];
