return [{ json: { jobs: [], source: 'dice', note: 'Dice parser needs API endpoint - see sticky note' } }];

// Filter out jobs we've already seen
const allJobs = $('ğŸ”€ Merge All Jobs').all();
const seenJobs = $('ğŸ“‹ Read Seen Jobs').all();

const seenIds = new Set(seenJobs.map(j => j.json.job_id || j.json.url || j.json.link));

const newJobs = allJobs.filter(job => {
  const jobId = job.json.job_id || job.json.url || job.json.link || job.json.title;
  return !seenIds.has(jobId);
});

console.log(`Found ${allJobs.length} total jobs, ${newJobs.length} are new`);

return newJobs.length > 0 ? newJobs : [{ json: { no_new_jobs: true } }];

