// Combine job data with AI analysis
const job = $('ğŸ”„ Loop Each Job').item.json;
const aiResponse = $input.first().json;

let analysis = {};
try {
  // Try to parse AI response as JSON
  const text = aiResponse.output || aiResponse.text || JSON.stringify(aiResponse);
  const jsonMatch = text.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    analysis = JSON.parse(jsonMatch[0]);
  }
} catch (e) {
  analysis = {
    fit_score: 5,
    matching_skills: [],
    gaps: [],
    resume_suggestions: aiResponse.output || 'AI analysis failed',
    recruiter_email: null
  };
}

return [{
  json: {
    job_id: job.job_id || job.url || job.link || `${job.title}-${job.company}`,
    title: job.title,
    company: job.company,
    location: job.location,
    description: (job.description || '').substring(0, 500) + '...',
    apply_link: job.url || job.link || job.applyUrl,
    recruiter_email: analysis.recruiter_email,
    fit_score: analysis.fit_score,
    matching_skills: (analysis.matching_skills || []).join(', '),
    gaps: (analysis.gaps || []).join(', '),
    resume_suggestions: analysis.resume_suggestions,
    source: job.source || 'unknown',
    scraped_at: new Date().toISOString()
  }
}];

